import React, { useEffect, useState } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

function InvoiceApp() {
  const [orders, setOrders] = useState([]);
  const [selectedOrderIndex, setSelectedOrderIndex] = useState(0);
  const [pdfDownloaded, setPdfDownloaded] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:3007/orders")
      .then((res) => {
        setOrders(res.data);
      })
      .catch((err) => {
        console.log("Error fetching data", err);
        alert("Error fetching API");
      });
  }, []);

  const generatePDF = () => {
    const order = orders[selectedOrderIndex];
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Siddha AI", 14, 22);
    doc.setFontSize(14);
    doc.text("INVOICE", 14, 30);
    doc.line(14, 32, 200, 32);

    doc.setFontSize(12);
    doc.text("Bill To:", 14, 40);
    doc.text(`${order.billTo.name}`, 14, 46);
    doc.text(`${order.billTo.email}`, 14, 52);
    doc.text(`${order.billTo.address}`, 14, 58);

    doc.text("Invoice Details:", 120, 40);
    doc.text(`Invoice : ${order.invoiceNumber}`, 120, 46);
    doc.text(`Date: ${order.date}`, 120, 52);
    doc.text(`Status: ${order.status}`, 120, 58);

    const tableData = order.items.map((item) => [
      item.item,
      item.quantity,
      `INR ${item.unitPrice.toFixed(2)}`,
      `INR ${(item.unitPrice * item.quantity).toFixed(2)}`
    ]);

    autoTable(doc, {
      startY: 70,
      head: [["Item", "Qty", "Unit Price", "Total"]],
      body: tableData,
      theme: "grid",
    });

    const subtotal = order.items.reduce(
      (acc, item) => acc + item.unitPrice * item.quantity,
      0
    );
    const tax = 10;
    const total = subtotal + tax;

    const finalY = doc.lastAutoTable.finalY + 10;
    const labelX = 149;
    const amountX = 1;

    doc.setFontSize(12);
    doc.setTextColor(0, 0, 0);
    doc.text("Subtotal:", labelX, finalY);
    doc.text(`INR ${subtotal.toFixed(2)}`, amountX, finalY, { align: "right" });

    doc.text("Tax (Flat):", labelX, finalY + 6);
    doc.text(`INR ${tax.toFixed(2)}`, amountX, finalY + 6, { align: "right" });

    doc.setFontSize(14);
    doc.setTextColor(0, 102, 204); // blue
    doc.text("Total:", labelX, finalY + 12);
    doc.text(`INR ${total.toFixed(2)}`, amountX, finalY + 12, { align: "right" });

    doc.setFontSize(11);
    doc.setTextColor(100, 100, 100);
    doc.text("If you have any questions, feel free to reach out to us.", 14, finalY + 25);

    doc.save("invoice.pdf");
    setPdfDownloaded(true);
  };

  if (orders.length === 0) return <div>Loading...</div>;
  const order = orders[selectedOrderIndex];

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Invoice Preview</h1>
      <div style={{ marginBottom: "20px" }}>
        <h3>Invoice Number: {order.invoiceNumber}</h3>
        <p><strong>Name:</strong> {order.billTo.name}</p>
        <p><strong>Email:</strong> {order.billTo.email}</p>
        <p><strong>Address:</strong> {order.billTo.address}</p>
        <p><strong>Date:</strong> {order.date}</p>
        <p><strong>Status:</strong> {order.status}</p>
      </div>

      <h2>Order Items</h2>
      <table style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Item</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Quantity</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Unit Price</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Total</th>
          </tr>
        </thead>
        <tbody>
          {order.items.map((item, idx) => (
            <tr key={idx}>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{item.item}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>{item.quantity}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>₹{item.unitPrice.toFixed(2)}</td>
              <td style={{ border: "1px solid #ccc", padding: "8px" }}>₹{(item.unitPrice * item.quantity).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <button
        onClick={generatePDF}
        style={{
          marginTop: "20px",
          backgroundColor: pdfDownloaded ? "#28a745" : "#007bff",
          color: "white",
          border: "none",
          padding: "10px 20px",
          cursor: "pointer",
          fontSize: "16px",
          borderRadius: "5px",
        }}
      >
        {pdfDownloaded ? "PDF Downloaded" : "Generate PDF"}
      </button>

      <div style={{ marginTop: "20px" }}>
        <label htmlFor="orderSelect">Choose Order: </label>
        <select
          id="orderSelect"
          value={selectedOrderIndex}
          onChange={(e) => setSelectedOrderIndex(Number(e.target.value))}
        >
          {orders.map((ord, index) => (
            <option key={ord.id} value={index}>
              {ord.invoiceNumber} - {ord.billTo.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default InvoiceApp;
