import React, { useEffect, useState } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import "jspdf-autotable";
import autoTable from "jspdf-autotable";

function InvoiceApp() {
  const [order, setOrder] = useState(null);
  const [pdfGenerated, setPdfGenerated] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:3007/orders")
      .then((res) => {
        setOrder(res.data[0]);
      })
      .catch((err) => {
        console.log("Error fetching data", err);
        alert("Error fetching API");
      });
  }, []);

  const generatePDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Siddha AI", 14, 22);
    doc.setFontSize(14);
    doc.text("INVOICE", 14, 30);
    doc.line(14, 32, 200, 32);

    doc.setFontSize(12);
    doc.text("Bill To:", 14, 40);
    doc.text(`${order.billTo.name}`, 14, 46);
    doc.text(`${order.billTo.email}`, 14, 52);
    doc.text(`${order.billTo.address}`, 14, 58);

    doc.text("Invoice Details:", 120, 40);
    doc.text(`Invoice : ${order.invoiceNumber}`, 120, 46);
    doc.text(`Date: ${order.date}`, 120, 52);
    doc.text(`Status: ${order.status}`, 120, 58);

    const tableData = order.items.map((item) => [
      item.item,
      item.quantity,
      `₹${item.unitPrice}`,
      `₹${(item.unitPrice * item.quantity).toFixed(2)}`
    ]);

    autoTable(doc, {
      startY: 70,
      head: [["Item", "Qty", "Unit Price", "Total"]],
      body: tableData
    });

    const subtotal = order.items.reduce(
      (acc, item) => acc + item.unitPrice * item.quantity,
      0
    );

    const finalY = doc.lastAutoTable.finalY + 10;
    doc.text(`Subtotal: ₹${subtotal.toFixed(2)}`, 150, finalY);
    doc.text(`Tax (0%): ₹0.00`, 150, finalY + 6);
    doc.setFontSize(14);
    doc.setTextColor(0, 0, 255);
    doc.text(`Total: ₹${subtotal.toFixed(2)}`, 150, finalY + 15);

    doc.setFontSize(11);
    doc.setTextColor(0, 0, 0);
    doc.text("Thank you!", 14, finalY + 25);

    doc.save("invoice.pdf");
    setPdfGenerated(true);
  };

  if (!order) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Invoice Preview</h1>
      <h3>{order.invoiceNumber} - {order.billTo.name}</h3>
      <button
        onClick={generatePDF}
        style={{
          backgroundColor: pdfGenerated ? "#28a745" : "#007bff",
          color: "white",
          padding: "10px 20px",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer"
        }}
      >
        {pdfGenerated ? "PDF Downloaded" : "Generate PDF"}
      </button>

      <div style={{ marginTop: "30px" }}>
        <h2>Order Items</h2>
        <table border="1" cellPadding="10" cellSpacing="0">
          <thead>
            <tr>
              <th>Item</th>
              <th>Quantity</th>
              <th>Unit Price</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {order.items.map((item, index) => (
              <tr key={index}>
                <td>{item.item}</td>
                <td>{item.quantity}</td>
                <td>₹{item.unitPrice}</td>
                <td>₹{(item.unitPrice * item.quantity).toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default InvoiceApp;
